package com.tianyou.OfferInterview.MyInterview.Rabbit;public class Rabbit {    /**     * 一对兔子三个月生一对，生一对的兔子三个月也能生一对，2年后一共有多少对兔子     * @param times     * @return     */    public int getRubbitCount(int times){        int count=1;        if(times==0||times==1){            return 1;        }        for(int i=1;i<=times;i++){            count+=count;        }        return count;    }}